import React from 'react'
import { useLocation } from "react-router-dom";
import Login from '../components/Login';

import { loggedIn, setTokens, getAccessToken } from '../utils/localstorage';
import { checkExpired } from '../utils/spotify'
const Home = () => {
    const location = useLocation();
    const params = new URLSearchParams(location.hash);
    var accessToken = params.get("accessToken")
    var refreshToken = params.get("refreshToken")
    var expiresIn = params.get("expiresIn")

    console.log(params)
    if (accessToken != null && refreshToken != null && expiresIn != null) {
        setTokens(accessToken, refreshToken)
    }

    if (loggedIn()) {
        ({ accessToken } = getAccessToken());
        checkExpired()
        return (
            <div>
                {accessToken}
            </div>
        )
    }
    else return (<Login />)
}

export default Home
