import { getTokenTimestamp } from './localstorage'

export function checkExpired(expiresIn){
    const expiration_time = expiresIn * 1000;
    const timeStamp = getTokenTimestamp()
    if (timeStamp != null && Date.now() - timeStamp > expiration_time) {
        console.warn('Access token has expired, refreshing...');
        // refreshAccessToken();
    }
}
